(function executeAction(current) {
    var util = new GlobalIncidentBacklogUtil();
    var result = util.createStoryInTeamSprint(current.sys_id.toString(), current.getTableName());

    if (result && result.sys_id) {
        var storyGR = new GlideRecord('rm_story');
        if (storyGR.get(result.sys_id)) {
            var storyLink = '<a href="' + storyGR.getLink() + '" target="_blank">' + storyGR.number + '</a>';
            var iterationText = result.iterationName ? ' in sprint ' + result.iterationName : '';

            if (result.status === 'existing') {
                gs.addInfoMessage('Story ' + storyLink + ' already exists' + iterationText + '.');
            } else {
                gs.addInfoMessage('SC Task is linked to Story ' + storyLink + iterationText + '.');
            }
        }
    } else {
        gs.addErrorMessage('Failed to create or find story. Check logs for details.');
    }

    return true; // prevents "undefined" UI Action error
})(current);




